function convertToHtml(){const e=document.getElementById("markdownInput").value,t=new window.markdownit,n=t.render(e),a=/\{\{([^}{]+)}}\s*\[\[([^\ยง]+?)\]\]/g,r=/\{\{\{([^}{]+)}}}\s*\[\[([^\ยง]+?)\]\]/g,o=/<img\b(.*?)>/g,d=n.replace(a,'<div id="t$1" class="hideme">$2</div>').replace(r,'<div id="t$1" class="showme">$2</div>').replace(o,'<img $1 class="responsive-image">');document.getElementById("htmlOutput").innerHTML=d}const textarea=document.getElementById("markdownInput");textarea.addEventListener("keyup",function(e){convertToHtml(),textarea.style.height="auto",textarea.style.height=textarea.scrollHeight+"px"}),fetch("sandbox.txt").then(e=>e.text()).then(e=>{var t=e.replace(/\r\n/g,"\n");document.getElementById("markdownInput").value=t}).catch(e=>{console.log("An error occurred:",e)});